router.post('/verify', async (req, res) => {
  const { accountNumber, signature } = req.body;

  try {
      // Fetch the stored signature for the account number from the database
      const storedSignatureRecord = await Signature.findOne({ accountNumber });
      if (!storedSignatureRecord) {
          return res.status(404).send({ message: 'Account not found' });
      }

      // Decode stored signature (base64) to a temporary file
      const storedSignaturePath = `temp_stored_${Date.now()}.jpg`;
      fs.writeFileSync(storedSignaturePath, Buffer.from(storedSignatureRecord.signature, 'base64'));

      // Decode verifying signature (base64) to a temporary file
      const verifyingSignaturePath = `temp_verifying_${Date.now()}.jpg`;
      fs.writeFileSync(verifyingSignaturePath, Buffer.from(signature, 'base64'));

      // Send files to Python microservice for verification
      const formData = new FormData();
      formData.append('accountNumber', accountNumber); // Optional: Send account number
      formData.append('stored_signature', fs.createReadStream(storedSignaturePath));
      formData.append('verifying_signature', fs.createReadStream(verifyingSignaturePath));

      const pythonResponse = await axios.post('http://localhost:5000/api/signature/verify', formData, {
          headers: formData.getHeaders(),
      });

      // Cleanup temporary files
      fs.unlinkSync(storedSignaturePath);
      fs.unlinkSync(verifyingSignaturePath);

      // Send verification result back to the client
      res.status(200).send(pythonResponse.data);
  } catch (err) {
      console.error(err);
      res.status(500).send({ message: 'Error during verification', error: err.message });
  }
});


  return (
    <div>
      <div className="verification-container">
        <div className="verification-box">
          <h1 className="verification-title">VERIFICATION</h1>
          <p className="verification-message">
            Please provide your account details and signature image for verification.
          </p>

          {/* Account Number Input */}
          <label className="input-label" htmlFor="account-number">
            Enter your account number:
          </label>
          <input
            type="text"
            id="account-number"
            className="input-field"
            value={accountNumber}
            onChange={(e) => setAccountNumber(e.target.value)}
          />
          <button
            className="go-home-btn"
            onClick={handleAccountVerification}
            disabled={!accountNumber.trim()}
          >
            Verify Account
          </button>
          {error && <p className="error-message">{error}</p>}

          {/* Image Upload Field */}
          <label className="input-label" htmlFor="signature-image">
            Upload your signature:
          </label>
          <input
            type="file"
            id="signature-image"
            className="input-field"
            accept="image/*"
            onChange={handleImageUpload}
            disabled={!isAccountValid}
          />

          {/* Verify Button */}
          <button
            className="go-home-btn"
            onClick={handleVerify}
            disabled={!isAccountValid || !image}
          >
            Verify
          </button>

         
        </div>
      </div>
    </div>
  );
};

export default VerificationPage;
