import React, { useState } from 'react';
import './VerificationPage.css';

const VerificationPage = () => {
  const [accountNumber, setAccountNumber] = useState('');
  const [isAccountValid, setIsAccountValid] = useState(false);
  const [image, setImage] = useState(null);
  const [storedSignatureBase64, setStoredSignatureBase64] = useState('');
  const [verificationResult, setVerificationResult] = useState('');
  const [error, setError] = useState('');

  // Function to verify account number with backend and fetch stored image
  const handleAccountVerification = async () => {
    setError('');  // Clear previous error messages

    if (!accountNumber.trim()) {
        setError('Please enter a valid account number.');
        return;
    }

    try {
        const response = await fetch('http://localhost:5000/api/auth/check-account', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ accountNumber }),
        });

        const result = await response.json();

        if (response.status === 200) {
            setIsAccountValid(true);
            setError('');  // Clear error messages on success
            alert(result.message); // Show success message
        } else {
            setIsAccountValid(false);
            setError(result.message || 'Invalid account number.');
        }
    } catch (err) {
        console.error('Error verifying account:', err);
        setError('An error occurred while verifying the account. Please try again later.');
    }
};

  // const handleAccountVerification = async () => {
  //   setError('');  // Clear previous error messages

  //   if (!accountNumber.trim()) {
  //     setError('Please enter a valid account number.');
  //     return;
  //   }

  //   try {
  //     // Verify account and fetch the stored image from the backend
  //     const response = await fetch('http://localhost:5000/api/signature/verify-account', {
  //       method: 'POST',
  //       headers: { 'Content-Type': 'application/json' },
  //       body: JSON.stringify({ accountNumber }),
  //     });

  //     const result = await response.json();

  //     if (response.status === 200) {
  //       setIsAccountValid(true);
  //       setStoredSignatureBase64(result.signature);  // Set stored base64 image
  //       setError('');  // Clear error messages on success
  //     } else {
  //       setIsAccountValid(false);
  //       setError(result.message || 'Invalid account number.');
  //     }
  //   } catch (err) {
  //     console.error('Error verifying account:', err);
  //     setError('An error occurred while verifying the account. Please try again later.');
  //   }
  // };

  // Handle signature image upload and convert to base64
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setImage(reader.result.split(',')[1]); // Extract base64 part of the image
    };
    if (file) {
      reader.readAsDataURL(file); // Convert image to base64
    }
  };

  // Handle verification of signature
  const handleVerify = async () => {
    const formData = new FormData();
    formData.append("accountNumber", accountNumber); // Add the account number
    formData.append("signature", image); // Add the verifying signature image
  
    try {
      const response = await fetch("http://localhost:5000/api/signature/verify", {
        method: "POST",
        body: formData,
      });
  
      const data = await response.json();
  
      // Log the response data to check if it has similarity and result
      console.log("Response Data: ", data);
  
      if (data.result && data.similarity !== undefined) {
        alert(`Result: ${data.result}\nSimilarity: ${data.similarity}`);
      } else {
        alert("Error: Invalid response from server.");
      }
    } catch (error) {
      console.log("Error verifying signature:", error);
    }
  };
  
  return (
    <div>
      <div className="verification-container">
        <div className="verification-box">
          <h1 className="verification-title">VERIFICATION</h1>
          <p className="verification-message">
            Please provide your account details and signature image for verification.
          </p>

          {/* Account Number Input */}
          <label className="input-label" htmlFor="account-number">
            Enter your account number:
          </label>
          <input
            type="text"
            id="account-number"
            className="input-field"
            value={accountNumber}
            onChange={(e) => setAccountNumber(e.target.value)}
          />
          <button
            className="go-home-btn"
            onClick={handleAccountVerification}
            disabled={!accountNumber.trim()}
          >
            Verify Account
          </button>
          {error && <p className="error-message">{error}</p>}

          {/* Image Upload Field */}
          <label className="input-label" htmlFor="signature-image">
            Upload your signature:
          </label>
          <input
            type="file"
            id="signature-image"
            className="input-field"
            accept="image/*"
            onChange={handleImageUpload}
            disabled={!isAccountValid}
          />

          {/* Verify Button */}
          <button
            className="go-home-btn"
            onClick={handleVerify}
            disabled={!isAccountValid || !image}
          >
            Verify
          </button>

          {/* Display verification result */}
          {verificationResult && <p>{verificationResult}</p>}
        </div>
      </div>
    </div>
  );
};

export default VerificationPage;
