import React, { useState } from 'react';
import './VerificationPage.css';

const VerificationPage = () => {
  const [accountNumber, setAccountNumber] = useState('');
  const [isAccountValid, setIsAccountValid] = useState(false);
  const [image, setImage] = useState(null);
  const [verificationStatus, setVerificationStatus] = useState('');
  const [error, setError] = useState('');
  const [storedSignature, setStoredSignature] = useState(null); // To hold the stored signature image path

  // Function to verify account number with backend
  const handleAccountVerification = async () => {
    setError('');  // Clear previous error messages

    if (!accountNumber.trim()) {
      setError('Please enter a valid account number.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/auth/check-account', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ accountNumber }),
      });

      const result = await response.json();

      if (response.status === 200) {
        setIsAccountValid(true);
        setError('');  // Clear error messages on success
        alert(result.message); // Show success message
        
        // Fetch the stored signature image path for the account
        const signatureResponse = await fetch(`http://localhost:5000/api/account/${accountNumber}/signature`);
        const signatureData = await signatureResponse.json();

        if (signatureResponse.status === 200 && signatureData.signaturePath) {
          // Set the stored signature image path
          setStoredSignature(signatureData.signaturePath);
        } else {
          setError('Stored signature not found for this account.');
          setIsAccountValid(false);
        }
      } else {
        setIsAccountValid(false);
        setError(result.message || 'Invalid account number.');
      }
    } catch (err) {
      console.error('Error verifying account:', err);
      setError('An error occurred while verifying the account. Please try again later.');
    }
  };

  // Handle signature image upload
  const handleImageUpload = (e) => {
    setImage(e.target.files[0]);
  };

  // Handle verification
  const handleVerify = async () => {
    // if (!storedSignature || !image) {
    //   setError('Both stored signature and verifying signature are required for verification.');
    //   return;
    // }

    const formData = new FormData();
    
    // Append verifying_signature to FormData (uploaded image)
    formData.append("verifying_signature", image);

    // Fetch the stored signature image and append it to FormData
    try {
      const response = await fetch(storedSignature); // Fetch the stored signature image from the server
      const blob = await response.blob(); // Convert to Blob format

      // Append the stored signature (image) to the form data
      formData.append("stored_signature", blob, 'stored_signature.png');
      
      // Send the form data to the backend for verification
      const verifyResponse = await fetch("http://127.0.0.1:5000/api/signature/verify", {
        method: "POST",
        body: formData,
      });

      const data = await verifyResponse.json();
      if (data.result) {
        alert(`Result: ${data.result}\nSimilarity: ${data.similarity}`);
      } else {
        setError('Signature verification failed.');
      }
    } catch (error) {
      console.log("Error verifying signature:", error);
      setError('An error occurred during signature verification. Please try again.');
    }
  };

  return (
    <div>
      <div className="verification-container">
        <div className="verification-box">
          <h1 className="verification-title">VERIFICATION</h1>
          <p className="verification-message">
            Please provide your account details and signature image for verification.
          </p>

          {/* Account Number Input */}
          <label className="input-label" htmlFor="account-number">
            Enter your account number:
          </label>
          <input
            type="text"
            id="account-number"
            className="input-field"
            value={accountNumber}
            onChange={(e) => setAccountNumber(e.target.value)}
          />
          <button
            className="go-home-btn"
            onClick={handleAccountVerification}
            disabled={!accountNumber.trim()}
          >
            Verify Account
          </button>
          {error && <p className="error-message">{error}</p>}

          {/* Image Upload Field */}
          <label className="input-label" htmlFor="signature-image">
            Upload your signature:
          </label>
          <input
            type="file"
            id="signature-image"
            className="input-field"
            accept="image/*"
            onChange={handleImageUpload}
            disabled={!isAccountValid}
          />

          {/* Verify Button */}
          <button
            className="go-home-btn"
            onClick={handleVerify}
            disabled={!isAccountValid || !image}
          >
            Verify
          </button>
        </div>
      </div>
    </div>
  );
};

export default VerificationPage;
